function openNav() {
  document.getElementById("nav").style.marginTop = "-250px";
  document.getElementById("top-nav").style.height = "250px";
  document.getElementById("main").style.marginTop = "250px";
}

function closeNav() {
  document.getElementById("nav").style.marginTop = "0"
  document.getElementById("top-nav").style.height = "0";
  document.getElementById("main").style.marginTop = "0";
}